void decode(char *hello){
     int k;
     for(k=0;k<3;k++){
        switch(hello[k]){
            case 'A':
                cursor_x_velo = 2;
                break;
            case 'B':
                cursor_x_velo = 2;
                break;
            case 'C':
                cursor_x_velo = 1;
                break;
            case 'D':
                cursor_x_velo = 1;
                break;
            case 'E':
                cursor_x_velo = 0;
                break;
            case 'F':
                cursor_x_velo = -1;
                break;
            case 'G':
                cursor_x_velo = -1;
                break;
            case 'H':
                cursor_x_velo = -2;
                break;
            case 'I':
                cursor_x_velo = -2;
                break;
            case 'J':
                cursor_y_velo = 2;
                break;
            case 'K':
                cursor_y_velo = 2;
                break;
            case 'L':
                cursor_y_velo = 1;
                break;
            case 'M':
                cursor_y_velo = 1;
                break;
            case 'N':
                cursor_y_velo = 0;
                break;
            case 'O':
                cursor_y_velo = -1;
                break;
            case 'P':
                cursor_y_velo = -1;
                break;
            case 'Q':
                cursor_y_velo = -2;
                break;
            case 'R':
                cursor_y_velo = -2;
                break;
            case 'S':
                bpress = 1;
                break;
            case 'T':
                 bpress = 1;
                break;
            case 'U':
                 bpress = 1;
                break;
            case 'V':
                 bpress = 1;
                break;
            case 'W':
                bpress = 0;
                break;
        }
    }  
}


